     1â†’/**
     2â†’ * UI-independent plugin manager service
     3â†’ *
     4â†’ * Manages plugin discovery, installation, and version tracking across:
     5â†’ * - Remote GitHub marketplaces
     6â†’ * - Local marketplace caches
     7â†’ * - Per-scope (user/project/local) plugin states
     8â†’ *
     9â†’ * UI-agnostic design:
    10â†’ * - No console.log or chalk dependencies
    11â†’ * - Optional logger callback for messages
    12â†’ * - Optional progress callback for long operations
    13â†’ * - Explicit projectPath parameter (no process.cwd())
    14â†’ * - All types and interfaces exported
    15â†’ */
    16â†’
    17â†’import path from "node:path";
    18â†’import os from "node:os";
    19â†’import {
    20â†’  getConfiguredMarketplaces,
    21â†’  getEnabledPlugins,
    22â†’  readSettings,
    23â†’  writeSettings,
    24â†’  readGlobalSettings,
    25â†’  writeGlobalSettings,
    26â†’  getGlobalConfiguredMarketplaces,
    27â†’  getGlobalEnabledPlugins,
    28â†’  getGlobalInstalledPluginVersions,
    29â†’  getLocalEnabledPlugins,
    30â†’  getLocalInstalledPluginVersions,
    31â†’  updateInstalledPluginsRegistry,
    32â†’  removeFromInstalledPluginsRegistry,
    33â†’} from "./claude-settings.js";
    34â†’import { NamespacedCacheManager } from "./cache-manager.js";
    35â†’import { CacheInvalidationHooks, createStandardHooks } from "./cache-hooks.js";
    36â†’
    37â†’// Path constants for local marketplaces
    38â†’const MARKETPLACES_DIR = path.join(os.homedir(), ".claude", "plugins", "marketplaces");
    39â†’const KNOWN_MARKETPLACES_FILE = path.join(os.homedir(), ".claude", "plugins", "known_marketplaces.json");
    40â†’import { defaultMarketplaces } from "../data/marketplaces.js";
    41â†’import {
    42â†’  scanLocalMarketplaces,
    43â†’  refreshLocalMarketplaces,
    44â†’  repairAllMarketplaces,
    45â†’  type LocalMarketplace,
    46â†’  type RefreshResult,
    47â†’  type ProgressCallback,
    48â†’  type RepairMarketplaceResult,
    49â†’  type ComponentMeta,
    50â†’} from "./local-marketplace.js";
    51â†’import {
    52â†’  formatMarketplaceName,
    53â†’  isValidGitHubRepo,
    54â†’  parsePluginId,
    55â†’} from "../utils/string-utils.js";
    56â†’import { validatePluginId } from "../utils/validators.js";
    57â†’
    58â†’// Export types for consumers
    59â†’export type { RefreshResult, ProgressCallback, RepairMarketplaceResult };
    60â†’
    61â†’// Logger callback for UI-independent logging
    62â†’export type LoggerCallback = (level: "info" | "warn" | "error", message: string, ...args: unknown[]) => void;
    63â†’
    64â†’// Cache for local marketplaces (session-level) - Promise-based to prevent race conditions
    65â†’let localMarketplacesPromise: Promise<Map<string, LocalMarketplace>> | null = null;
    66â†’
    67â†’// Enhanced caching layer with LRU eviction and invalidation hooks
    68â†’const pluginCache = new NamespacedCacheManager<unknown>({
    69â†’  maxSize: 500, // Max 500 entries per namespace
    70â†’  ttl: 5 * 60 * 1000, // 5 minute default TTL
    71â†’  enableStats: true,
    72â†’});
    73â†’
    74â†’// Cache invalidation hooks
    75â†’const cacheHooks = createStandardHooks(pluginCache);
    76â†’
    77â†’export interface ScopeStatus {
    78â†’  enabled: boolean;
    79â†’  version?: string;
    80â†’}
    81â†’
    82â†’export interface PluginInfo {
    83â†’  id: string;
    84â†’  name: string;
    85â†’  version: string | null;
    86â†’  description: string;
    87â†’  marketplace: string;
    88â†’  marketplaceDisplay: string;
    89â†’  enabled: boolean;
    90â†’  installedVersion?: string;
    91â†’  hasUpdate?: boolean;
    92â†’  // Per-scope installation status
    93â†’  userScope?: ScopeStatus;
    94â†’  projectScope?: ScopeStatus;
    95â†’  localScope?: ScopeStatus;
    96â†’  // Extended info
    97â†’  category?: string;
    98â†’  author?: { name: string; email?: string };
    99â†’  homepage?: string;
   100â†’  tags?: string[];
   101â†’  source?: string | { source: string; url?: string };
   102â†’  agents?: ComponentMeta[];
   103â†’  commands?: ComponentMeta[];
   104â†’  skills?: ComponentMeta[];
   105â†’  mcpServers?: string[];
   106â†’  lspServers?: Record<string, unknown>;
   107â†’}
   108â†’
   109â†’export interface MarketplacePlugin {
   110â†’  name: string;
   111â†’  version: string | null;
   112â†’  description: string;
   113â†’  category?: string;
   114â†’  author?: { name: string; email?: string };
   115â†’  homepage?: string;
   116â†’  tags?: string[];
   117â†’}
   118â†’
   119â†’// Session-level cache for fetched marketplace data (no TTL - persists until explicit refresh)
   120â†’const marketplaceCache = new Map<string, MarketplacePlugin[]>();
   121â†’
   122â†’/**
   123â†’ * Fetch plugins from a remote GitHub marketplace
   124â†’ * @param marketplaceName - Display name of marketplace
   125â†’ * @param repo - GitHub repository (format: "owner/repo")
   126â†’ * @param logger - Optional logger callback
   127â†’ * @returns Array of marketplace plugins (empty on error)
   128â†’ */
   129â†’export async function fetchMarketplacePlugins(
   130â†’  marketplaceName: string,
   131â†’  repo: string,
   132â†’  logger?: LoggerCallback,
   133â†’): Promise<MarketplacePlugin[]> {
   134â†’  // Check enhanced cache first - with TTL and LRU eviction
   135â†’  const cacheKey = `marketplace:${marketplaceName}`;
   136â†’  const cached = pluginCache.get('marketplace', cacheKey) as MarketplacePlugin[] | undefined;
   137â†’  if (cached) {
   138â†’    return cached;
   139â†’  }
   140â†’
   141â†’  // Fallback to old cache for backwards compatibility during transition
   142â†’  const legacyCached = marketplaceCache.get(marketplaceName);
   143â†’  if (legacyCached) {
   144â†’    // Migrate to new cache
   145â†’    pluginCache.set('marketplace', cacheKey, legacyCached);
   146â†’    return legacyCached;
   147â†’  }
   148â†’
   149â†’  // Validate repo format to prevent SSRF
   150â†’  if (!isValidGitHubRepo(repo)) {
   151â†’    logger?.("error", `Invalid GitHub repo format: ${repo}`);
   152â†’    return [];
   153â†’  }
   154â†’
   155â†’  try {
   156â†’    // Fetch marketplace.json from GitHub
   157â†’    const url = `https://raw.githubusercontent.com/${repo}/main/.claude-plugin/marketplace.json`;
   158â†’    const response = await fetch(url, {
   159â†’      signal: AbortSignal.timeout(10000), // 10s timeout
   160â†’    });
   161â†’
   162â†’    if (!response.ok) {
   163â†’      logger?.("error", `Failed to fetch marketplace: ${response.status}`);
   164â†’      return [];
   165â†’    }
   166â†’
   167â†’    // Validate content-type
   168â†’    const contentType = response.headers.get("content-type");
   169â†’    if (
   170â†’      contentType &&
   171â†’      !contentType.includes("application/json") &&
   172â†’      !contentType.includes("text/plain")
   173â†’    ) {
   174â†’      logger?.("error", `Invalid content-type for marketplace: ${contentType}`);
   175â†’      return [];
   176â†’    }
   177â†’
   178â†’    interface RawPlugin {
   179â†’      name: string;
   180â†’      version?: string | null;
   181â†’      description?: string;
   182â†’      category?: string;
   183â†’      author?: { name: string; email?: string };
   184â†’      homepage?: string;
   185â†’      tags?: string[];
   186â†’    }
   187â†’    const data = (await response.json()) as { plugins?: RawPlugin[] };
   188â†’    const plugins: MarketplacePlugin[] = [];
   189â†’
   190â†’    if (data.plugins && Array.isArray(data.plugins)) {
   191â†’      for (const plugin of data.plugins) {
   192â†’        plugins.push({
   193â†’          name: plugin.name,
   194â†’          version: plugin.version || null,
   195â†’          description: plugin.description || "",
   196â†’          category: plugin.category,
   197â†’          author: plugin.author,
   198â†’          homepage: plugin.homepage,
   199â†’          tags: plugin.tags,
   200â†’        });
   201â†’      }
   202â†’    }
   203â†’
   204â†’    // Cache the result in both old and new cache
   205â†’    marketplaceCache.set(marketplaceName, plugins);
   206â†’    pluginCache.set('marketplace', cacheKey, plugins);
   207â†’
   208â†’    return plugins;
   209â†’  } catch (error) {
   210â†’    logger?.("error", `Error fetching marketplace ${marketplaceName}:`, error);
   211â†’    return [];
   212â†’  }
   213â†’}
   214â†’
   215â†’/**
   216â†’ * Get all available plugins across all marketplaces (with per-scope status)
   217â†’ * @param projectPath - Absolute path to project (optional, uses global scope if not provided)
   218â†’ * @param logger - Optional logger callback
   219â†’ * @returns Array of plugin info with scope-specific status
   220â†’ */
   221â†’export async function getAvailablePlugins(
   222â†’  projectPath?: string,
   223â†’  logger?: LoggerCallback,
   224â†’): Promise<PluginInfo[]> {
   225â†’  const configuredMarketplaces = projectPath
   226â†’    ? await getConfiguredMarketplaces(projectPath)
   227â†’    : await getGlobalConfiguredMarketplaces();
   228â†’  const enabledPlugins = projectPath
   229â†’    ? await getEnabledPlugins(projectPath)
   230â†’    : await getGlobalEnabledPlugins();
   231â†’  const installedVersions = projectPath
   232â†’    ? await getInstalledPluginVersions(projectPath)
   233â†’    : await getGlobalInstalledPluginVersions();
   234â†’
   235â†’  // Fetch all scopes for per-scope status
   236â†’  const userEnabledPlugins = await getGlobalEnabledPlugins();
   237â†’  const userInstalledVersions = await getGlobalInstalledPluginVersions();
   238â†’  const projectEnabledPlugins = projectPath
   239â†’    ? await getEnabledPlugins(projectPath)
   240â†’    : {};
   241â†’  const projectInstalledVersions = projectPath
   242â†’    ? await getInstalledPluginVersions(projectPath)
   243â†’    : {};
   244â†’  const localEnabledPlugins = projectPath
   245â†’    ? await getLocalEnabledPlugins(projectPath)
   246â†’    : {};
   247â†’  const localInstalledVersions = projectPath
   248â†’    ? await getLocalInstalledPluginVersions(projectPath)
   249â†’    : {};
   250â†’
   251â†’  const plugins: PluginInfo[] = [];
   252â†’  const seenPluginIds = new Set<string>();
   253â†’
   254â†’  // Helper to build scope status
   255â†’  const buildScopeStatus = (pluginId: string) => ({
   256â†’    userScope:
   257â†’      userEnabledPlugins[pluginId] !== undefined
   258â†’        ? {
   259â†’            enabled: userEnabledPlugins[pluginId],
   260â†’            version: userInstalledVersions[pluginId],
   261â†’          }
   262â†’        : undefined,
   263â†’    projectScope:
   264â†’      projectEnabledPlugins[pluginId] !== undefined
   265â†’        ? {
   266â†’            enabled: projectEnabledPlugins[pluginId],
   267â†’            version: projectInstalledVersions[pluginId],
   268â†’          }
   269â†’        : undefined,
   270â†’    localScope:
   271â†’      localEnabledPlugins[pluginId] !== undefined
   272â†’        ? {
   273â†’            enabled: localEnabledPlugins[pluginId],
   274â†’            version: localInstalledVersions[pluginId],
   275â†’          }
   276â†’        : undefined,
   277â†’  });
   278â†’
   279â†’  // Get all marketplace names (configured + official/featured defaults)
   280â†’  // Always include official and featured marketplaces so users can browse them
   281â†’  const marketplaceNames = new Set<string>();
   282â†’  for (const mp of defaultMarketplaces) {
   283â†’    if (configuredMarketplaces[mp.name] || mp.official || mp.featured) {
   284â†’      marketplaceNames.add(mp.name);
   285â†’    }
   286â†’  }
   287â†’
   288â†’  // Pre-fetch local marketplaces for merging detailed plugin info (agents, commands, skills, mcpServers)
   289â†’  const localMarketplaces = await getLocalMarketplaces();
   290â†’
   291â†’  // Fetch plugins from each configured marketplace
   292â†’  for (const mpName of marketplaceNames) {
   293â†’    const marketplace = defaultMarketplaces.find((m) => m.name === mpName);
   294â†’    if (!marketplace || !marketplace.source.repo) continue;
   295â†’
   296â†’    const marketplacePlugins = await fetchMarketplacePlugins(
   297â†’      mpName,
   298â†’      marketplace.source.repo,
   299â†’      logger,
   300â†’    );
   301â†’
   302â†’    // Check if we have a local clone for this marketplace (for detailed plugin info)
   303â†’    const localMp = localMarketplaces.get(mpName);
   304â†’
   305â†’    for (const plugin of marketplacePlugins) {
   306â†’      const pluginId = `${plugin.name}@${mpName}`;
   307â†’      const installedVersion = installedVersions[pluginId];
   308â†’      const isEnabled = enabledPlugins[pluginId] === true;
   309â†’      const scopeStatus = buildScopeStatus(pluginId);
   310â†’
   311â†’      // Try to get detailed info from local marketplace clone
   312â†’      const localPlugin = localMp?.plugins.find((p) => p.name === plugin.name);
   313â†’
   314â†’      seenPluginIds.add(pluginId);
   315â†’      plugins.push({
   316â†’        id: pluginId,
   317â†’        name: plugin.name,
   318â†’        version: plugin.version,
   319â†’        description: plugin.description,
   320â†’        marketplace: mpName,
   321â†’        marketplaceDisplay: marketplace.displayName,
   322â†’        enabled: isEnabled,
