   629â†’/**
   630â†’ * Simple version comparison
   631â†’ * @param a - Version string (with optional "v" prefix)
   632â†’ * @param b - Version string (with optional "v" prefix)
   633â†’ * @returns 1 if a > b, -1 if a < b, 0 if equal
   634â†’ */
   635â†’function compareVersions(
   636â†’  a: string | null | undefined,
   637â†’  b: string | null | undefined,
   638â†’): number {
   639â†’  if (!a || !b) return 0;
   640â†’  const partsA = a.replace(/^v/, "").split(".").map(Number);
   641â†’  const partsB = b.replace(/^v/, "").split(".").map(Number);
   642â†’
   643â†’  for (let i = 0; i < Math.max(partsA.length, partsB.length); i++) {
   644â†’    const numA = partsA[i] || 0;
   645â†’    const numB = partsB[i] || 0;
   646â†’    if (numA > numB) return 1;
   647â†’    if (numA < numB) return -1;
   648â†’  }
   649â†’  return 0;
   650â†’}
