  createStandardHooks,
  type InvalidationRule,
  type HookTrigger,
} from './services/cache-hooks.js';

// Services - Plugin Management
export {
  // Plugin listing
  getAvailablePlugins,
  getGlobalAvailablePlugins,
  fetchMarketplacePlugins,
  getLocalMarketplacesInfo,
  // Plugin version management
  saveInstalledPluginVersion,
