   359â†’
   360â†’/**
   361â†’ * Namespaced cache manager for organizing related cache entries
   362â†’ *
   363â†’ * @example
   364â†’ * ```typescript
   365â†’ * const cache = new NamespacedCacheManager({ maxSize: 500 });
   366â†’ * cache.set('users', 'user:123', userData);
   367â†’ * cache.get('users', 'user:123');
   368â†’ * cache.invalidateNamespace('users'); // Clear all user cache
   369â†’ * ```
   370â†’ */
   371â†’export class NamespacedCacheManager<T = unknown> {
   372â†’  private caches = new Map<string, CacheManager<T>>();
   373â†’  private globalOptions: CacheOptions;
   374â†’
   375â†’  constructor(options: CacheOptions = {}) {
   376â†’    this.globalOptions = options;
   377â†’  }
   378â†’
   379â†’  /**
   380â†’   * Get or create a cache for a namespace
   381â†’   * @param namespace - Namespace identifier
   382â†’   * @returns Cache manager for namespace
   383â†’   */
   384â†’  private getCache(namespace: string): CacheManager<T> {
   385â†’    let cache = this.caches.get(namespace);
   386â†’    if (!cache) {
   387â†’      cache = new CacheManager<T>(this.globalOptions);
   388â†’      this.caches.set(namespace, cache);
   389â†’    }
   390â†’    return cache;
   391â†’  }
   392â†’
   393â†’  /**
   394â†’   * Get value from namespaced cache
   395â†’   */
   396â†’  get(namespace: string, key: string): T | undefined {
   397â†’    return this.getCache(namespace).get(key);
   398â†’  }
   399â†’
   400â†’  /**
   401â†’   * Set value in namespaced cache
   402â†’   */
   403â†’  set(namespace: string, key: string, value: T, options?: { ttl?: number }): void {
   404â†’    this.getCache(namespace).set(key, value, options);
   405â†’  }
   406â†’
   407â†’  /**
   408â†’   * Check if key exists in namespace
   409â†’   */
   410â†’  has(namespace: string, key: string): boolean {
   411â†’    return this.getCache(namespace).has(key);
   412â†’  }
   413â†’
   414â†’  /**
   415â†’   * Delete key from namespace
   416â†’   */
   417â†’  delete(namespace: string, key: string): boolean {
   418â†’    return this.getCache(namespace).delete(key);
   419â†’  }
   420â†’
   421â†’  /**
   422â†’   * Invalidate by pattern within namespace
   423â†’   */
   424â†’  invalidate(namespace: string, pattern: string): number {
   425â†’    return this.getCache(namespace).invalidate(pattern);
   426â†’  }
   427â†’
   428â†’  /**
   429â†’   * Clear entire namespace
   430â†’   */
   431â†’  invalidateNamespace(namespace: string): void {
   432â†’    const cache = this.caches.get(namespace);
   433â†’    if (cache) {
   434â†’      cache.clear();
   435â†’      this.caches.delete(namespace);
   436â†’    }
   437â†’  }
   438â†’
   439â†’  /**
   440â†’   * Clear all namespaces
   441â†’   */
   442â†’  clear(): void {
   443â†’    // First clear each cache's contents
   444â†’    for (const cache of this.caches.values()) {
   445â†’      cache.clear();
   446â†’    }
   447â†’    // Then remove all namespace entries
   448â†’    this.caches.clear();
   449â†’  }
   450â†’
   451â†’  /**
   452â†’   * Get statistics for a namespace
   453â†’   */
   454â†’  getStats(namespace: string): CacheStats | undefined {
   455â†’    const cache = this.caches.get(namespace);
   456â†’    return cache?.getStats();
   457â†’  }
   458â†’
   459â†’  /**
   460â†’   * Get statistics for all namespaces
   461â†’   */
   462â†’  getAllStats(): Record<string, CacheStats> {
   463â†’    const stats: Record<string, CacheStats> = {};
   464â†’    for (const [namespace, cache] of this.caches) {
   465â†’      stats[namespace] = cache.getStats();
   466â†’    }
   467â†’    return stats;
   468â†’  }
   469â†’
   470â†’  /**
   471â†’   * Register invalidation hook for a namespace
   472â†’   */
   473â†’  onInvalidate(namespace: string, hook: InvalidationHook): () => void {
   474â†’    return this.getCache(namespace).onInvalidate(hook);
   475â†’  }
   476â†’
   477â†’  /**
   478â†’   * Get all namespaces
   479â†’   */
   480â†’  namespaces(): string[] {
   481â†’    return Array.from(this.caches.keys());
   482â†’  }
   483â†’
   484â†’  /**
   485â†’   * Get total size across all namespaces
   486â†’   */
   487â†’  size(): number {
   488â†’    let total = 0;
   489â†’    for (const cache of this.caches.values()) {
   490â†’      total += cache.size();
   491â†’    }
   492â†’    return total;
   493â†’  }
   494â†’}
   495â†’

