     1â†’/**
     2â†’ * UI-independent plugin manager service
     3â†’ *
     4â†’ * Manages plugin discovery, installation, and version tracking across:
     5â†’ * - Remote GitHub marketplaces
     6â†’ * - Local marketplace caches
     7â†’ * - Per-scope (user/project/local) plugin states
     8â†’ *
     9â†’ * UI-agnostic design:
    10â†’ * - No console.log or chalk dependencies
    11â†’ * - Optional logger callback for messages
    12â†’ * - Optional progress callback for long operations
    13â†’ * - Explicit projectPath parameter (no process.cwd())
    14â†’ * - All types and interfaces exported
    15â†’ */
    16â†’
    17â†’import path from "node:path";
    18â†’import os from "node:os";
    19â†’import {
    20â†’  getConfiguredMarketplaces,
    21â†’  getEnabledPlugins,
    22â†’  readSettings,
    23â†’  writeSettings,
    24â†’  readGlobalSettings,
    25â†’  writeGlobalSettings,
    26â†’  getGlobalConfiguredMarketplaces,
    27â†’  getGlobalEnabledPlugins,
    28â†’  getGlobalInstalledPluginVersions,
    29â†’  getLocalEnabledPlugins,
    30â†’  getLocalInstalledPluginVersions,
    31â†’  updateInstalledPluginsRegistry,
    32â†’  removeFromInstalledPluginsRegistry,
    33â†’  enablePlugin as enablePluginInternal,
    34â†’  enableLocalPlugin as enableLocalPluginInternal,
    35â†’  enableGlobalPlugin as enableGlobalPluginInternal,
    36â†’} from "./claude-settings.js";
    37â†’import { NamespacedCacheManager } from "./cache-manager.js";
    38â†’import { CacheInvalidationHooks, createStandardHooks } from "./cache-hooks.js";
    39â†’
    40â†’// Path constants for local marketplaces
    41â†’const MARKETPLACES_DIR = path.join(os.homedir(), ".claude", "plugins", "marketplaces");
    42â†’const KNOWN_MARKETPLACES_FILE = path.join(os.homedir(), ".claude", "plugins", "known_marketplaces.json");
    43â†’import { defaultMarketplaces } from "../data/marketplaces.js";
    44â†’import {
    45â†’  scanLocalMarketplaces,
    46â†’  refreshLocalMarketplaces,
    47â†’  repairAllMarketplaces,
    48â†’  type LocalMarketplace,
    49â†’  type RefreshResult,
    50â†’  type ProgressCallback,
    51â†’  type RepairMarketplaceResult,
    52â†’  type ComponentMeta,
    53â†’} from "./local-marketplace.js";
    54â†’import {
    55â†’  formatMarketplaceName,
    56â†’  isValidGitHubRepo,
    57â†’  parsePluginId,
    58â†’} from "../utils/string-utils.js";
    59â†’import { validatePluginId } from "../utils/validators.js";
    60â†’
    61â†’// Export types for consumers
    62â†’export type { RefreshResult, ProgressCallback, RepairMarketplaceResult };
    63â†’
    64â†’// Logger callback for UI-independent logging
    65â†’export type LoggerCallback = (level: "info" | "warn" | "error", message: string, ...args: unknown[]) => void;
    66â†’
    67â†’// Cache for local marketplaces (session-level) - Promise-based to prevent race conditions
    68â†’let localMarketplacesPromise: Promise<Map<string, LocalMarketplace>> | null = null;
    69â†’
    70â†’// Enhanced caching layer with LRU eviction and invalidation hooks
    71â†’const pluginCache = new NamespacedCacheManager<unknown>({
    72â†’  maxSize: 500, // Max 500 entries per namespace
    73â†’  ttl: 5 * 60 * 1000, // 5 minute default TTL
    74â†’  enableStats: true,
    75â†’});
    76â†’
    77â†’// Cache invalidation hooks
    78â†’const cacheHooks = createStandardHooks(pluginCache);
    79â†’
    80â†’export interface ScopeStatus {
    81â†’  enabled: boolean;
    82â†’  version?: string;
    83â†’}
    84â†’
    85â†’export interface PluginInfo {
    86â†’  id: string;
    87â†’  name: string;
    88â†’  version: string | null;
    89â†’  description: string;
    90â†’  marketplace: string;
    91â†’  marketplaceDisplay: string;
    92â†’  enabled: boolean;
    93â†’  installedVersion?: string;
    94â†’  hasUpdate?: boolean;
    95â†’  // Per-scope installation status
    96â†’  userScope?: ScopeStatus;
    97â†’  projectScope?: ScopeStatus;
    98â†’  localScope?: ScopeStatus;
    99â†’  // Extended info
   100â†’  category?: string;
   101â†’  author?: { name: string; email?: string };
   102â†’  homepage?: string;
   103â†’  tags?: string[];
   104â†’  source?: string | { source: string; url?: string };
   105â†’  agents?: ComponentMeta[];
   106â†’  commands?: ComponentMeta[];
   107â†’  skills?: ComponentMeta[];
   108â†’  mcpServers?: string[];
   109â†’  lspServers?: Record<string, unknown>;
   110â†’}
   111â†’
   112â†’export interface MarketplacePlugin {
   113â†’  name: string;
   114â†’  version: string | null;
   115â†’  description: string;
   116â†’  category?: string;
   117â†’  author?: { name: string; email?: string };
   118â†’  homepage?: string;
   119â†’  tags?: string[];
   120â†’}
   121â†’
   122â†’// Session-level cache for fetched marketplace data (no TTL - persists until explicit refresh)
   123â†’const marketplaceCache = new Map<string, MarketplacePlugin[]>();
   124â†’
   125â†’/**
   126â†’ * Fetch plugins from a remote GitHub marketplace
   127â†’ * @param marketplaceName - Display name of marketplace
   128â†’ * @param repo - GitHub repository (format: "owner/repo")
   129â†’ * @param logger - Optional logger callback
   130â†’ * @returns Array of marketplace plugins (empty on error)
   131â†’ */
   132â†’export async function fetchMarketplacePlugins(
   133â†’  marketplaceName: string,
   134â†’  repo: string,
   135â†’  logger?: LoggerCallback,
   136â†’): Promise<MarketplacePlugin[]> {
   137â†’  // Check enhanced cache first - with TTL and LRU eviction
   138â†’  const cacheKey = `marketplace:${marketplaceName}`;
   139â†’  const cached = pluginCache.get('marketplace', cacheKey) as MarketplacePlugin[] | undefined;
   140â†’  if (cached) {
   141â†’    return cached;
   142â†’  }
   143â†’
   144â†’  // Fallback to old cache for backwards compatibility during transition
   145â†’  const legacyCached = marketplaceCache.get(marketplaceName);
   146â†’  if (legacyCached) {
   147â†’    // Migrate to new cache
   148â†’    pluginCache.set('marketplace', cacheKey, legacyCached);
   149â†’    return legacyCached;
   150â†’  }
   151â†’
   152â†’  // Validate repo format to prevent SSRF
   153â†’  if (!isValidGitHubRepo(repo)) {
   154â†’    logger?.("error", `Invalid GitHub repo format: ${repo}`);
   155â†’    return [];
   156â†’  }
   157â†’
   158â†’  try {
   159â†’    // Fetch marketplace.json from GitHub
   160â†’    const url = `https://raw.githubusercontent.com/${repo}/main/.claude-plugin/marketplace.json`;
   161â†’    const response = await fetch(url, {
   162â†’      signal: AbortSignal.timeout(10000), // 10s timeout
   163â†’    });
   164â†’
   165â†’    if (!response.ok) {
   166â†’      logger?.("error", `Failed to fetch marketplace: ${response.status}`);
   167â†’      return [];
   168â†’    }
   169â†’
   170â†’    // Validate content-type
   171â†’    const contentType = response.headers.get("content-type");
   172â†’    if (
   173â†’      contentType &&
   174â†’      !contentType.includes("application/json") &&
   175â†’      !contentType.includes("text/plain")
   176â†’    ) {
   177â†’      logger?.("error", `Invalid content-type for marketplace: ${contentType}`);
   178â†’      return [];
   179â†’    }
   180â†’
   181â†’    interface RawPlugin {
   182â†’      name: string;
   183â†’      version?: string | null;
   184â†’      description?: string;
   185â†’      category?: string;
   186â†’      author?: { name: string; email?: string };
   187â†’      homepage?: string;
   188â†’      tags?: string[];
   189â†’    }
   190â†’    const data = (await response.json()) as { plugins?: RawPlugin[] };
   191â†’    const plugins: MarketplacePlugin[] = [];
   192â†’
   193â†’    if (data.plugins && Array.isArray(data.plugins)) {
   194â†’      for (const plugin of data.plugins) {
   195â†’        plugins.push({
   196â†’          name: plugin.name,
   197â†’          version: plugin.version || null,
   198â†’          description: plugin.description || "",
   199â†’          category: plugin.category,
   200â†’          author: plugin.author,
   201â†’          homepage: plugin.homepage,
   202â†’          tags: plugin.tags,
   203â†’        });
   204â†’      }
   205â†’    }
   206â†’
   207â†’    // Cache the result in both old and new cache
   208â†’    marketplaceCache.set(marketplaceName, plugins);
   209â†’    pluginCache.set('marketplace', cacheKey, plugins);
   210â†’
   211â†’    return plugins;
   212â†’  } catch (error) {
   213â†’    logger?.("error", `Error fetching marketplace ${marketplaceName}:`, error);
   214â†’    return [];
   215â†’  }
   216â†’}
   217â†’
   218â†’/**
   219â†’ * Get all available plugins across all marketplaces (with per-scope status)
   220â†’ * @param projectPath - Absolute path to project (optional, uses global scope if not provided)
   221â†’ * @param logger - Optional logger callback
   222â†’ * @returns Array of plugin info with scope-specific status
   223â†’ */
   224â†’export async function getAvailablePlugins(
   225â†’  projectPath?: string,
   226â†’  logger?: LoggerCallback,
   227â†’): Promise<PluginInfo[]> {
   228â†’  const configuredMarketplaces = projectPath
   229â†’    ? await getConfiguredMarketplaces(projectPath)
   230â†’    : await getGlobalConfiguredMarketplaces();
   231â†’  const enabledPlugins = projectPath
   232â†’    ? await getEnabledPlugins(projectPath)
   233â†’    : await getGlobalEnabledPlugins();
   234â†’  const installedVersions = projectPath
   235â†’    ? await getInstalledPluginVersions(projectPath)
   236â†’    : await getGlobalInstalledPluginVersions();
   237â†’
   238â†’  // Fetch all scopes for per-scope status
   239â†’  const userEnabledPlugins = await getGlobalEnabledPlugins();
   240â†’  const userInstalledVersions = await getGlobalInstalledPluginVersions();
   241â†’  const projectEnabledPlugins = projectPath
   242â†’    ? await getEnabledPlugins(projectPath)
   243â†’    : {};
   244â†’  const projectInstalledVersions = projectPath
   245â†’    ? await getInstalledPluginVersions(projectPath)
   246â†’    : {};
   247â†’  const localEnabledPlugins = projectPath
   248â†’    ? await getLocalEnabledPlugins(projectPath)
   249â†’    : {};
   250â†’  const localInstalledVersions = projectPath
   251â†’    ? await getLocalInstalledPluginVersions(projectPath)
   252â†’    : {};
   253â†’
   254â†’  const plugins: PluginInfo[] = [];
   255â†’  const seenPluginIds = new Set<string>();
   256â†’
   257â†’  // Helper to build scope status
   258â†’  const buildScopeStatus = (pluginId: string) => ({
   259â†’    userScope:
   260â†’      userEnabledPlugins[pluginId] !== undefined
   261â†’        ? {
   262â†’            enabled: userEnabledPlugins[pluginId],
   263â†’            version: userInstalledVersions[pluginId],
   264â†’          }
   265â†’        : undefined,
   266â†’    projectScope:
   267â†’      projectEnabledPlugins[pluginId] !== undefined
   268â†’        ? {
   269â†’            enabled: projectEnabledPlugins[pluginId],
   270â†’            version: projectInstalledVersions[pluginId],
   271â†’          }
   272â†’        : undefined,
   273â†’    localScope:
   274â†’      localEnabledPlugins[pluginId] !== undefined
   275â†’        ? {
   276â†’            enabled: localEnabledPlugins[pluginId],
   277â†’            version: localInstalledVersions[pluginId],
   278â†’          }
   279â†’        : undefined,
   280â†’  });
   281â†’
   282â†’  // Get all marketplace names (configured + official/featured defaults)
   283â†’  // Always include official and featured marketplaces so users can browse them
   284â†’  const marketplaceNames = new Set<string>();
   285â†’  for (const mp of defaultMarketplaces) {
   286â†’    if (configuredMarketplaces[mp.name] || mp.official || mp.featured) {
   287â†’      marketplaceNames.add(mp.name);
   288â†’    }
   289â†’  }
   290â†’
   291â†’  // Pre-fetch local marketplaces for merging detailed plugin info (agents, commands, skills, mcpServers)
   292â†’  const localMarketplaces = await getLocalMarketplaces();
   293â†’
   294â†’  // Fetch plugins from each configured marketplace
   295â†’  for (const mpName of marketplaceNames) {
   296â†’    const marketplace = defaultMarketplaces.find((m) => m.name === mpName);
   297â†’    if (!marketplace || !marketplace.source.repo) continue;
   298â†’
   299â†’    const marketplacePlugins = await fetchMarketplacePlugins(
   300â†’      mpName,
   301â†’      marketplace.source.repo,
   302â†’      logger,
   303â†’    );
   304â†’
   305â†’    // Check if we have a local clone for this marketplace (for detailed plugin info)
   306â†’    const localMp = localMarketplaces.get(mpName);
   307â†’
   308â†’    for (const plugin of marketplacePlugins) {
   309â†’      const pluginId = `${plugin.name}@${mpName}`;
   310â†’      const installedVersion = installedVersions[pluginId];
   311â†’      const isEnabled = enabledPlugins[pluginId] === true;
   312â†’      const scopeStatus = buildScopeStatus(pluginId);
   313â†’
   314â†’      // Try to get detailed info from local marketplace clone
   315â†’      const localPlugin = localMp?.plugins.find((p) => p.name === plugin.name);
   316â†’
   317â†’      seenPluginIds.add(pluginId);
   318â†’      plugins.push({
   319â†’        id: pluginId,
   320â†’        name: plugin.name,
   321â†’        version: plugin.version,
