    32â†’  /** Timestamp when entry was created */
    33â†’  createdAt: number;
    34â†’  /** Timestamp when entry was last accessed */
    35â†’  accessedAt: number;
    36â†’  /** Timestamp when entry expires (undefined = no expiration) */
    37â†’  expiresAt?: number;
    38â†’}
    39â†’
    40â†’export interface CacheStats {
    41â†’  /** Total number of get() calls */
    42â†’  hits: number;
    43â†’  /** Number of get() calls that returned a value */
    44â†’  misses: number;
    45â†’  /** Current number of entries in cache */
    46â†’  size: number;
    47â†’  /** Maximum allowed entries */
    48â†’  maxSize: number;
    49â†’  /** Number of entries evicted due to LRU */
