    65â†’  private maxSize: number;
    66â†’  private ttl?: number;
    67â†’  private enableStats: boolean;
    68â†’
    69â†’  // Statistics
    70â†’  private stats = {
    71â†’    hits: 0,
    72â†’    misses: 0,
    73â†’    evictions: 0,
    74â†’    expirations: 0,
