   286â†’  }
   287â†’
   288â†’  /**
   289â†’   * Get cache size
   290â†’   * @returns Number of entries in cache
   291â†’   */
   292â†’  size(): number {
   293â†’    return this.cache.size;
   294â†’  }
   295â†’
   296â†’  /**
