   227â†’  // Private methods
   228â†’
   229â†’  private async trigger(
   230â†’    trigger: HookTrigger,
   231â†’    context?: Record<string, unknown>,
   232â†’  ): Promise<void> {
   233â†’    const rules = this.rules.get(trigger);
   234â†’    if (!rules || rules.length === 0) return;
   235â†’
   236â†’    for (const rule of rules) {
   237â†’      // Check condition if provided
   238â†’      if (rule.condition) {
   239â†’        const shouldInvalidate = await rule.condition();
   240â†’        if (!shouldInvalidate) continue;
   241â†’      }
   242â†’
   243â†’      // Invalidate all patterns
   244â†’      for (const pattern of rule.patterns) {
   245â†’        // Replace context variables in pattern
   246â†’        const resolvedPattern = this.resolvePattern(pattern, context);
   247â†’        this.invalidatePattern(resolvedPattern);
   248â†’      }
   249â†’    }
   250â†’  }
   251â†’
   252â†’  private resolvePattern(
   253â†’    pattern: string,
   254â†’    context?: Record<string, unknown>,
   255â†’  ): string {
   256â†’    if (!context) return pattern;
   257â†’
   258â†’    let resolved = pattern;
   259â†’    for (const [key, value] of Object.entries(context)) {
   260â†’      resolved = resolved.replace(`{${key}}`, String(value));
   261â†’    }
   262â†’    return resolved;
   263â†’  }
   264â†’
   265â†’  private invalidatePattern(pattern: string): void {
   266â†’    if (this.isNamespaced) {
   267â†’      // Handle wildcard pattern that clears everything
   268â†’      if (pattern === '*') {
   269â†’        (this.cache as NamespacedCacheManager).clear();
   270â†’        return;
   271â†’      }
   272â†’
   273â†’      // For namespaced cache, parse namespace from pattern
   274â†’      const [namespace, ...keyParts] = pattern.split(':');
   275â†’      if (keyParts.length > 0) {
   276â†’        const keyPattern = keyParts.join(':');
   277â†’        (this.cache as NamespacedCacheManager).invalidate(namespace, keyPattern);
   278â†’      } else {
   279â†’        // Clear entire namespace
   280â†’        (this.cache as NamespacedCacheManager).invalidateNamespace(namespace);
   281â†’      }
   282â†’    } else {
   283â†’      // For regular cache, invalidate by pattern
   284â†’      (this.cache as CacheManager).invalidate(pattern);
   285â†’    }
   286â†’  }
   287â†’}
