   334â†’class ScopedInvalidator {
   335â†’  private hooks: CacheInvalidationHooks;
   336â†’  private namespace: string;
   337â†’
   338â†’  constructor(hooks: CacheInvalidationHooks, namespace: string) {
   339â†’    this.hooks = hooks;
   340â†’    this.namespace = namespace;
