   632â†’/**
   633â†’ * Simple version comparison
   634â†’ * @param a - Version string (with optional "v" prefix)
   635â†’ * @param b - Version string (with optional "v" prefix)
   636â†’ * @returns 1 if a > b, -1 if a < b, 0 if equal
   637â†’ */
   638â†’function compareVersions(
   639â†’  a: string | null | undefined,
   640â†’  b: string | null | undefined,
   641â†’): number {
   642â†’  if (!a || !b) return 0;
   643â†’  const partsA = a.replace(/^v/, "").split(".").map(Number);
   644â†’  const partsB = b.replace(/^v/, "").split(".").map(Number);
   645â†’
   646â†’  for (let i = 0; i < Math.max(partsA.length, partsB.length); i++) {
   647â†’    const numA = partsA[i] || 0;
   648â†’    const numB = partsB[i] || 0;
   649â†’    if (numA > numB) return 1;
   650â†’    if (numA < numB) return -1;
   651â†’  }
   652â†’  return 0;
   653â†’}
