   788â†’// Marketplace discovery
   789â†’function discoverMarketplacesFromSettings(
   790â†’  settings: ClaudeSettings,
   791â†’): DiscoveredMarketplace[] {
   792â†’  const discovered = new Map<string, DiscoveredMarketplace>();
   793â†’
   794â†’  // From extraKnownMarketplaces
   795â†’  for (const [name, config] of Object.entries(
   796â†’    settings.extraKnownMarketplaces || {},
   797â†’  )) {
   798â†’    discovered.set(name, { name, source: "configured" as const, config });
   799â†’  }
   800â†’
   801â†’  // From enabledPlugins
   802â†’  for (const pluginId of Object.keys(settings.enabledPlugins || {})) {
   803â†’    const parsed = parsePluginId(pluginId);
   804â†’    if (parsed && !discovered.has(parsed.marketplace)) {
   805â†’      discovered.set(parsed.marketplace, {
   806â†’        name: parsed.marketplace,
   807â†’        source: "inferred" as const,
   808â†’      });
   809â†’    }
   810â†’  }
   811â†’
   812â†’  // From installedPluginVersions
   813â†’  for (const pluginId of Object.keys(settings.installedPluginVersions || {})) {
   814â†’    const parsed = parsePluginId(pluginId);
   815â†’    if (parsed && !discovered.has(parsed.marketplace)) {
   816â†’      discovered.set(parsed.marketplace, {
   817â†’        name: parsed.marketplace,
   818â†’        source: "inferred" as const,
   819â†’      });
   820â†’    }
   821â†’  }
   822â†’
   823â†’  return Array.from(discovered.values());
   824â†’}
