   860â†’// MCP server testing and status
   861â†’export async function testMcpConnection(
   862â†’  config: McpServerConfig,
   863â†’  _logger?: (message: string) => void,
   864â†’): Promise<McpTestResult> {
   865â†’  const steps: McpTestStep[] = [];
   866â†’
   867â†’  // Determine if this is an HTTP-based or command-based server
   868â†’  const isHttpServer = config.url && !config.command;
   869â†’
   870â†’  // Step 1: Validate config
   871â†’  if (!config.command && !config.url) {
   872â†’    steps.push({
   873â†’      step: "Validate config",
   874â†’      passed: false,
   875â†’      error: "Either command or url is required",
   876â†’    });
   877â†’    return {
   878â†’      success: false,
   879â†’      error: "Either command or url is required",
   880â†’      steps,
   881â†’    };
   882â†’  }
   883â†’  steps.push({
   884â†’    step: "Validate config",
   885â†’    passed: true,
   886â†’  });
   887â†’
   888â†’  // HTTP-based server testing
   889â†’  if (isHttpServer) {
   890â†’    // Step 2: Validate URL format
   891â†’    let url: URL;
   892â†’    try {
   893â†’      url = new URL(config.url!);
   894â†’      steps.push({
   895â†’        step: "URL format valid",
   896â†’        passed: true,
   897â†’      });
   898â†’    } catch {
   899â†’      steps.push({
   900â†’        step: "URL format valid",
   901â†’        passed: false,
   902â†’        error: `Invalid URL format: ${config.url}`,
   903â†’      });
   904â†’      return {
   905â†’        success: false,
   906â†’        error: `Invalid URL format: ${config.url}`,
   907â†’        steps,
   908â†’      };
   909â†’    }
   910â†’
   911â†’    // Step 3: Test HTTP connection with MCP protocol request
   912â†’    try {
   913â†’      const controller = new AbortController();
   914â†’      const timeout = setTimeout(() => controller.abort(), 15000);
   915â†’
   916â†’      // Send an MCP initialize request to properly test the connection
   917â†’      const mcpRequest = {
   918â†’        jsonrpc: "2.0",
   919â†’        id: 1,
   920â†’        method: "initialize",
   921â†’        params: {
   922â†’          protocolVersion: "2024-11-05",
   923â†’          capabilities: {},
   924â†’          clientInfo: {
   925â†’            name: "claudeup-test",
   926â†’            version: "1.0.0",
   927â†’          },
   928â†’        },
   929â†’      };
   930â†’
   931â†’      const response = await fetch(url.toString(), {
   932â†’        method: "POST",
   933â†’        signal: controller.signal,
   934â†’        headers: {
   935â†’          "Content-Type": "application/json",
   936â†’          "Accept": "application/json",
   937â†’        },
   938â†’        body: JSON.stringify(mcpRequest),
   939â†’      });
   940â†’      clearTimeout(timeout);
   941â†’
   942â†’      // Check authentication status
   943â†’      if (response.status === 401 || response.status === 403) {
   944â†’        steps.push({
   945â†’          step: "Server reachable",
   946â†’          passed: true,
   947â†’        });
   948â†’        steps.push({
   949â†’          step: "Authentication",
   950â†’          passed: false,
   951â†’          error: "Server requires authentication - configure the required API key/token",
   952â†’        });
   953â†’        return {
   954â†’          success: false,
   955â†’          error: "Authentication failed - configure the required environment variables",
   956â†’          steps,
   957â†’        };
   958â†’      }
   959â†’
   960â†’      // Server responded - check if it's a valid MCP response
   961â†’      steps.push({
   962â†’        step: "Server reachable",
   963â†’        passed: true,
   964â†’      });
   965â†’
   966â†’      if (response.ok) {
   967â†’        try {
   968â†’          const data = await response.json() as { result?: unknown; error?: { message?: string } };
   969â†’          if (data.result || data.error) {
   970â†’            // Valid MCP JSON-RPC response
   971â†’            if (data.error) {
   972â†’              steps.push({
   973â†’                step: "MCP handshake",
   974â†’                passed: false,
   975â†’                error: data.error.message || "Server returned an error",
   976â†’              });
   977â†’              return {
   978â†’                success: false,
   979â†’                error: data.error.message || "MCP handshake failed",
   980â†’                steps,
   981â†’              };
   982â†’            }
   983â†’            steps.push({
   984â†’              step: "MCP handshake",
   985â†’              passed: true,
   986â†’            });
   987â†’            return {
   988â†’              success: true,
   989â†’              steps,
   990â†’            };
   991â†’          }
   992â†’        } catch {
   993â†’          // Response wasn't JSON
   994â†’        }
   995â†’      }
   996â†’
   997â†’      // Non-200 response or invalid response
   998â†’      steps.push({
   999â†’        step: "MCP handshake",
  1000â†’        passed: false,
  1001â†’        error: `Server returned status ${response.status}`,
  1002â†’      });
  1003â†’      return {
  1004â†’        success: false,
  1005â†’        error: `Server returned unexpected status: ${response.status}`,
  1006â†’        steps,
  1007â†’      };
  1008â†’    } catch (error) {
  1009â†’      steps.push({
  1010â†’        step: "HTTP connection",
  1011â†’        passed: false,
  1012â†’        error: error instanceof Error ? error.message : "Failed to connect to server",
  1013â†’      });
  1014â†’      return {
  1015â†’        success: false,
  1016â†’        error: error instanceof Error ? error.message : "Failed to connect to server",
  1017â†’        steps,
  1018â†’      };
  1019â†’    }
  1020â†’  }
  1021â†’
  1022â†’  // Command-based server testing
  1023â†’  // Step 2: Check if command exists
  1024â†’  try {
  1025â†’    const isWindows = process.platform === "win32";
  1026â†’    const checkCommand = isWindows ? "where" : "which";
  1027â†’    await execFileAsync(checkCommand, [config.command!]);
  1028â†’    steps.push({
  1029â†’      step: "Command exists",
  1030â†’      passed: true,
  1031â†’    });
  1032â†’  } catch {
  1033â†’    steps.push({
  1034â†’      step: "Command exists",
  1035â†’      passed: false,
  1036â†’      error: `Command '${config.command}' not found in PATH`,
  1037â†’    });
  1038â†’    return {
  1039â†’      success: false,
  1040â†’      error: `Command '${config.command}' not found in PATH`,
  1041â†’      steps,
  1042â†’    };
  1043â†’  }
  1044â†’
  1045â†’  // Step 3: Try to spawn the process
  1046â†’  try {
  1047â†’    await new Promise<void>((resolve, reject) => {
  1048â†’      const proc = spawn(config.command!, config.args || [], {
  1049â†’        env: {
  1050â†’          PATH: process.env.PATH,
  1051â†’          HOME: process.env.HOME,
  1052â†’          ...config.env,
  1053â†’        },
  1054â†’        stdio: ["pipe", "pipe", "pipe"],
  1055â†’      });
  1056â†’
  1057â†’      const timeout = setTimeout(() => {
  1058â†’        proc.kill();
  1059â†’        reject(new Error("Process spawn timeout after 5 seconds"));
  1060â†’      }, 5000);
  1061â†’
  1062â†’      proc.on("error", (error) => {
  1063â†’        clearTimeout(timeout);
  1064â†’        reject(error);
  1065â†’      });
  1066â†’
  1067â†’      proc.on("spawn", () => {
  1068â†’        clearTimeout(timeout);
  1069â†’        proc.kill();
  1070â†’        resolve();
  1071â†’      });
  1072â†’    });
  1073â†’
  1074â†’    steps.push({
  1075â†’      step: "Process spawn",
  1076â†’      passed: true,
  1077â†’    });
  1078â†’
  1079â†’    return {
  1080â†’      success: true,
  1081â†’      steps,
  1082â†’    };
  1083â†’  } catch (error) {
