  1097â†’export async function getMcpServerStatus(
  1098â†’  serverName: string,
  1099â†’  projectPath: string,
  1100â†’): Promise<McpServerStatus> {
  1101â†’  try {
  1102â†’    validateProjectPath(projectPath);
  1103â†’
  1104â†’    // Check if server config exists
  1105â†’    const servers = await getInstalledMcpServers(projectPath);
  1106â†’    const serverConfig = servers[serverName];
  1107â†’    if (!serverConfig) {
  1108â†’      return {
  1109â†’        state: "error",
