  1120â†’        state: "stopped",
  1121â†’      };
  1122â†’    }
  1123â†’
  1124â†’    // For HTTP-based servers, check if URL is reachable
  1125â†’    const isHttpServer = serverConfig.url && !serverConfig.command;
  1126â†’    if (isHttpServer && serverConfig.url) {
  1127â†’      const serverUrl = serverConfig.url;
  1128â†’      try {
  1129â†’        const controller = new AbortController();
  1130â†’        const timeoutId = setTimeout(() => controller.abort(), 5000);
  1131â†’
  1132â†’        const response = await fetch(serverUrl, {
  1133â†’          method: "POST",
  1134â†’          headers: { "Content-Type": "application/json" },
  1135â†’          body: JSON.stringify({
  1136â†’            jsonrpc: "2.0",
  1137â†’            id: 1,
  1138â†’            method: "initialize",
  1139â†’            params: {
  1140â†’              protocolVersion: "2024-11-05",
  1141â†’              capabilities: {},
  1142â†’              clientInfo: { name: "claudeup-status", version: "1.0.0" },
  1143â†’            },
  1144â†’          }),
  1145â†’          signal: controller.signal,
  1146â†’        });
  1147â†’
  1148â†’        clearTimeout(timeoutId);
  1149â†’
  1150â†’        if (response.status === 401 || response.status === 403) {
  1151â†’          return {
  1152â†’            state: "error",
  1153â†’            error: "Authentication required - check environment variables",
  1154â†’          };
  1155â†’        }
  1156â†’
  1157â†’        if (response.ok) {
  1158â†’          return {
  1159â†’            state: "running",
  1160â†’          };
  1161â†’        }
  1162â†’
  1163â†’        return {
  1164â†’          state: "error",
  1165â†’          error: `Server returned ${response.status}`,
  1166â†’        };
  1167â†’      } catch (fetchError) {
  1168â†’        if (fetchError instanceof Error && fetchError.name === "AbortError") {
  1169â†’          return {
  1170â†’            state: "error",
  1171â†’            error: "Connection timeout",
  1172â†’          };
  1173â†’        }
  1174â†’        return {
  1175â†’          state: "error",
  1176â†’          error: "Server unreachable",
  1177â†’        };
  1178â†’      }
  1179â†’    }
  1180â†’
  1181â†’    // For command-based servers, return unknown (we don't track running processes)
  1182â†’    return {
  1183â†’      state: "unknown",
  1184â†’    };
  1185â†’  } catch (error) {
  1186â†’    return {
