  1194â†’export function validateMcpServerName(name: string): { valid: boolean; error?: string } {
  1195â†’  if (!name || typeof name !== "string") {
  1196â†’    return { valid: false, error: "Server name is required" };
  1197â†’  }
  1198â†’
  1199â†’  if (!name.match(/^[a-z0-9-_]+$/)) {
  1200â†’    return {
  1201â†’      valid: false,
  1202â†’      error: "Server name must contain only lowercase letters, numbers, hyphens, and underscores",
  1203â†’    };
  1204â†’  }
  1205â†’
  1206â†’  if (name.length > 64) {
  1207â†’    return { valid: false, error: "Server name must be 64 characters or less" };
  1208â†’  }
  1209â†’
  1210â†’  if (name.startsWith("-") || name.startsWith("_")) {
  1211â†’    return { valid: false, error: "Server name cannot start with hyphen or underscore" };
  1212â†’  }
