  1217â†’export function validateMcpServerConfig(config: McpServerConfig): { valid: boolean; errors: string[] } {
  1218â†’  const errors: string[] = [];
  1219â†’
  1220â†’  if (!config || typeof config !== "object") {
  1221â†’    errors.push("Server configuration is required");
  1222â†’    return { valid: false, errors };
  1223â†’  }
  1224â†’
  1225â†’  if (!config.command || typeof config.command !== "string") {
  1226â†’    errors.push("Server command is required");
  1227â†’  } else {
  1228â†’    // Security: Block command injection via shell metacharacters
  1229â†’    const DANGEROUS_CHARS = /[;&|`$()><\n\r]/;
  1230â†’    if (DANGEROUS_CHARS.test(config.command)) {
  1231â†’      errors.push("Command contains forbidden shell metacharacters (;, &, |, `, $, (, ), <, >, newlines)");
  1232â†’    }
  1233â†’  }
  1234â†’
  1235â†’  // Validate args
  1236â†’  if (config.args && !Array.isArray(config.args)) {
  1237â†’    errors.push("Args must be an array");
  1238â†’  } else if (config.args) {
  1239â†’    for (const arg of config.args) {
  1240â†’      if (typeof arg !== "string") {
  1241â†’        errors.push("All args must be strings");
  1242â†’        break;
  1243â†’      }
  1244â†’    }
  1245â†’  }
  1246â†’
  1247â†’  // Validate env vars
  1248â†’  if (config.env) {
  1249â†’    if (typeof config.env !== "object" || Array.isArray(config.env)) {
  1250â†’      errors.push("Env must be an object");
  1251â†’    } else {
  1252â†’      for (const [key, value] of Object.entries(config.env)) {
  1253â†’        if (typeof key !== "string" || typeof value !== "string") {
  1254â†’          errors.push("Env vars must be string key-value pairs");
  1255â†’          break;
  1256â†’        }
  1257â†’
  1258â†’        // Validate env var name format
  1259â†’        if (!key.match(/^[A-Z_][A-Z0-9_]*$/)) {
  1260â†’          errors.push(`Invalid env var name: ${key} (must be UPPER_CASE)`);
  1261â†’        }
  1262â†’      }
  1263â†’    }
  1264â†’  }
  1265â†’
  1266â†’  return { valid: errors.length === 0, errors };
  1267â†’}
